import React ,{Component} from "react";
import { connect } from "react-redux";
import {withRouter} from "react-router-dom";
import Userlist from "./Userlist";
import Userchat from "./Userchat";
import {getuserlist,getalluser,addNewUser,getuserchat,sendmsg} from '../../actions/chatActions'
import './style.css'




class index extends Component {

	state = {
		message:"",
	    userchat:[],
	    chatusers:[],
	    userList:[],
	    open:false,
	    searchkey:'',
	    searchpeopledata:[],
	    suggetionvalue:[],
	    alluser:[],
	}

	componentDidMount(){
	    this.props.getuserlist(this.props.history)
	    this.props.getalluser()
		// this.props.getuserchat(this.props.match.params.cid)
		let socket = this.props.socket;
		console.log(socket)
	    if (socket) {
	      	socket.emit("getchat", {
		        channelId:this.props.match.params.cid,
		    });
	    }

	    // return () => {
	    //   	//Component Unmount
	    //   	if (socket) {
	    //     	socket.emit("leaveRoom", {
	    //       		channelId:this.props.match.params.cid,
	    //     	});
	    //   	}
	    // };
	    // console.log(this.props)
	}

	componentDidUpdate(PrevProps){
	    // if(PrevProps.match.params.cid!==this.props.match.params.cid){
	    //   	this.props.getuserchat(this.props.match.params.cid)
	    // }
	}

	onchangeHandler = (val) => {
	    this.setState({message:val})
	}

	sendmsg = (e) => {
	    e.preventDefault();
	    let payload = {
			message:this.state.message,
			attachmentName:'',
			attachmentType:'',
			channelId:this.props.match.params.cid,
			receiver:this.props.match.params.id
	    }
	    this.props.sendmsg(payload)
	    this.setState({message:""})
	}



	render(){
		// console.log(this.props.history)
		// console.log(this.props.userchat)

		return (
		    <div className="container-fluid h-100">
				<div className="row justify-content-center h-100">
					<Userlist
						chatlistdata={this.props.chatlistdata}
						addNewUser={this.props.addNewUser}
						receiver={this.props.match.params.id}
					/>
					<Userchat
						userchat={this.props.userchat}
						onchangeHandler={this.onchangeHandler}
						message={this.state.message}
						sendmsg={this.sendmsg}
						receiver={this.props.match.params.id}
					/>
				</div>
			</div>
		  );	
	}

}

const mapStateToProps = (state, ownProps) => {
    return {
    	chatlistdata:state.chat,
    	userchat:state.chat.userchat,
    }
};

const mapDispatchToProps = dispatch => {
    return {
        getuserlist: (payload,callback) => dispatch(getuserlist(payload,callback)),
        getalluser: (payload,callback) => dispatch(getalluser(payload,callback)),
        addNewUser: (payload,callback) => dispatch(addNewUser(payload,callback)),
        getuserchat: (payload,callback) => dispatch(getuserchat(payload,callback)),
        sendmsg: (payload,callback) => dispatch(sendmsg(payload,callback)),
    };
  };

export default withRouter(connect(mapStateToProps,mapDispatchToProps)(index))
