import React,{Component} from 'react';
import axios from "axios";
import { withRouter } from "react-router-dom";
import { parse, stringify } from "query-string";
import Chatlist from './Chatlist'
import Chat from './Chat'
import './style.css'

class ChatContainer extends Component{

  state = {
    message:"",
    userchat:[],
    chatusers:[],
    userList:[],
    open:false,
    searchkey:'',
    searchpeopledata:[],
    suggetionvalue:[],
    alluser:[],
  }

  getChatedUserList = () => {
      let userId = localStorage.getItem("CC_userId");
      axios.get("http://localhost:8000/chatroom/chateduser",{
          headers:{
            Authorization:"Bearer "+localStorage.getItem("CC_Token"),
          }
      })
      .then(res=>{
        this.setState({userList:res.data})
        this.props.history.push("/newchat/"+res.data[0]._id+"/"+res.data[0].chatId)
    })
    .catch(err=>{
      // setTimeout(this.getChatedUserList,3000)
    })
  }

  getuserchat = (id,cid) => {
    axios.get("http://localhost:8000/chatroom/getuserchat/"+cid,{
        headers:{
          Authorization:"Bearer "+localStorage.getItem("CC_Token"),
        }
    })
    .then(res=>{
      // console.log(res.data)
      this.setState({userchat:res.data}) 
    })
    .catch(err=>{
      console.log(err)    
    })
  }

  componentDidMount(){
    this.getChatedUserList()
    this.getuserchat(this.props.match.params.id)
  }

  componentDidUpdate(PrevProps){
    if(PrevProps.match.params.id!==this.props.match.params.id){
      this.getuserchat(this.props.match.params.id)
    }
  }

  sendmsg = (e) => {
    e.preventDefault();
    let payload = {
      message:this.state.message,
      attachmentName:'',
      attachmentType:'',
      channelId:this.props.match.params.cid,
      receiver:this.props.match.params.id
    }
    axios.post("http://localhost:8000/chatroom/savechat",payload,{
        headers:{
          Authorization:"Bearer "+localStorage.getItem("CC_Token"),
        }
    })
    .then(res=>{
      this.setState({message:""})
    })
    .catch(err=>{
      console.log(err)    
    })
  }

  onchangeHandler = (val) => {
    this.setState({message:val})
  }

  selectuser = (item) => {
    console.log(item)
    let userList = [...this.state.userList]
    let payload = {
      _id:item._id,
      name:item.name,
      img:"",
      sendTIme:new Date()
    }
    userList.unshift(payload)
    this.setState({userList:userList})
    this.props.history.push("/newchat/"+item._id)
  }

  render(){
    return(
          <div id="container">
              <Chatlist selectuser={this.selectuser} state={this.state} sender={this.props.match.params.id}/>
              <Chat sendmsg={this.sendmsg} state={this.state} onchangeHandler={this.onchangeHandler} sender={this.props.match.params.id}/>
          </div>
      );
    }
}


export default withRouter(ChatContainer)
