import React ,{Component,useRef,useState} from "react"

export default function Newcompo(){
	const inputRef = useRef("")
	const inputRef1 = useRef("")
	const [state,setState] = useState({
		isEven:"",
		isPrime:"",
		number:"",
		number1:"",

	})

	function handleClick() {
		let num = inputRef.current.value%2;
		let isEven = "even"
		if(num===1){
			isEven="odd"
		}
		setState({
			...state,
			number:inputRef.current.value,
			isEven  :isEven
		})
	}

	function checkPrime() {
		console.log(inputRef1.current.value)
		let num = inputRef1.current.value%2;
		console.log(num===1 && inputRef1.current.value !== "1")
		let isPrime = "non prime";
		if(num===1 && parseInt(inputRef1.current.value) !== 1){
			isPrime="prime"
		}
		setState({
			...state,
			number1:inputRef1.current.value,
			isPrime  :isPrime
		})
	}



	return(
		<div>
			<p>Check odd or even number</p>
			<input type="number" ref={inputRef}/>
			<button onClick={handleClick}>Click me</button>
			{
				state.number &&
					<p>This number {state.number} is {state.isEven}</p>
			}
			<br/>
			<br/>
			<p>Check prime number</p>
			<input type="number" ref={inputRef1}/>
			<button onClick={checkPrime}>Click me</button>
			{
				state.number1 &&
					<p>This number {state.number1} is {state.isPrime}</p>
			}
		</div>
	)
}
