import React from 'react';
import axios from "axios";
import { Link } from "react-router-dom";


let people = [
	{
		id:1,
		name:"Gaurav",
		onlineStatus:true
	},
	{
		id:2,
		name:"Rahul",
		onlineStatus:false
	},
	{
		id:3,
		name:"Abhishek",
		onlineStatus:true
	},
	{
		id:4,
		name:"Mohit",
		onlineStatus:false
	},
	{
		id:5,
		name:"Ashish",
		onlineStatus:false
	},
]

const Chatlist = () => {
	const [open,setOpen] = React.useState(false)
	const [searchkey,setSearchkey] = React.useState('')
	const [searchpeopledata,setSearchpeopledata] = React.useState(people)
	const [suggetionvalue,setSuggetionvalue] = React.useState([])
	const [alluser,setAlluser] = React.useState([])

	const onchange = (e) => {
		if(e.target.value!==''){
			setSearchkey(e.target.value)
			setOpen(true)
			let suggetionVal = [];
			searchpeopledata.map(item=>{
		        let searchkeyval = e.target.value.toLowerCase();
		        let itemval = item.name.toLowerCase()
		        if(itemval.includes(searchkeyval)){
		          suggetionVal.push(item)
		        }
		    })
		    setSuggetionvalue(suggetionVal)
		}else{
			setOpen(false)
			setSearchkey("")
		}
	}

	const getUsers = () => {
	    axios.get("http://localhost:8000/user/getusers",{
	        headers:{
	          Authorization:"Bearer "+localStorage.getItem("CC_Token"),
	        }
	    })
        .then(res=>{
        	// setSearchpeopledata(res.data)
        	setAlluser(res.data)
        	let firstId = res.data[0]._id;

    	})
        .catch(err=>{
        	setTimeout(getUsers,3000)
        })
	}


	React.useEffect(()=>{
		getUsers()
	},[])

	const chatlist = (id) => {
		console.log(id)
	}


	return 	<aside>
	            <header>
	              <input 
	              	type="text"
	              	placeholder="search"
	              	onChange={onchange}
	              	value={searchkey}
	              />
	            </header>
	            {
	            	open ?
	            		<ul className="seachpopop">
	            		{
	            			suggetionvalue.map(item=>{
	            				return 	<li>
	            						<Link to={`asd`}>
							               <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/1940306/chat_avatar_09.jpg" alt=""/>
							               <div>
							               	  <h2>{item.name}</h2>
							               	  {
							               	  	// (item.onlineStatus) ?
							               	  	// 	<h3>
									                 //    <span className="status green"></span>
									                 //    online
									                 //  </h3>
							               	  	// :
							               	  	// 	<h3>
									                 //    <span className="status orange"></span>
									                 //    offline
									                 //  </h3>
							               	  }

							                </div>
							            </Link>
							            </li>
	            			})
	            		}
			            </ul>
	            	:null
	            }
	            <ul className="showuserlist">
	            {
	            	alluser &&
	            	alluser.map(item=>
	            		<li key={item._id}>
	            			<Link to={`/newchat/${item._id}`}>
				                <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/1940306/chat_avatar_09.jpg" alt=""/>
				                <div>
				                  	<h2>{item.name}</h2>
				                  	<h3>
				                    	<span className="status green"></span>
				                    	online
				                  	</h3>
			                	</div>
		                	</Link>
			            </li>
	            	)
	            }

	            </ul>
	        </aside>
}

export default Chatlist
