import React,{Component} from 'react';
import axios from "axios";
import { withRouter } from "react-router-dom";
import Chatlist from './Chatlist'
import Chat from './Chat'
import './style.css'

class ChatContainer extends Component{

  state = {
    message:"",
    userchat:[]
  }

  getuserchat = (id) => {
    axios.get("http://localhost:8000/chatroom/getuserchat/"+id,{
        headers:{
          Authorization:"Bearer "+localStorage.getItem("CC_Token"),
        }
    })
    .then(res=>{
      // console.log(res.data)
      this.setState({userchat:res.data}) 
    })
    .catch(err=>{
      console.log(err)    
    })
  }

  componentDidMount(){
    this.getuserchat(this.props.match.params.id)
  }

  componentDidUpdate(PrevProps){
    // console.log(PrevProps.match.params.id,this.props.match.params.id)
    if(PrevProps.match.params.id!==this.props.match.params.id){
      this.getuserchat(this.props.match.params.id)
    }
  }

  sendmsg = (e) => {
    e.preventDefault();
    let payload = {
      message:this.state.message,
      attachment:'',
      receiver:this.props.match.params.id
    }
    axios.post("http://localhost:8000/chatroom/savechat",payload,{
        headers:{
          Authorization:"Bearer "+localStorage.getItem("CC_Token"),
        }
    })
    .then(res=>{
      this.setState({message:""})
    })
    .catch(err=>{
      console.log(err)    
    })
  }

  onchangeHandler = (val) => {
    this.setState({message:val})
  }

  selectuserchat = () => {

  }

  render(){
    return(
          <div id="container">
              <Chatlist/>
              <Chat selectuserchat={this.selectuserchat} sendmsg={this.sendmsg} state={this.state} onchangeHandler={this.onchangeHandler}/>
          </div>
      );
    }
}


export default withRouter(ChatContainer)
