import React from 'react';


let people = [
	{
		id:1,
		name:"Gaurav",
		onlineStatus:true
	},
	{
		id:2,
		name:"Rahul",
		onlineStatus:false
	},
	{
		id:3,
		name:"Abhishek",
		onlineStatus:true
	},
	{
		id:4,
		name:"Mohit",
		onlineStatus:false
	},
	{
		id:5,
		name:"Ashish",
		onlineStatus:false
	},
]

const Chatlist = () => {
	const [open,setOpen] = React.useState(false)
	const [searchkey,setSearchkey] = React.useState('')
	const [searchpeopledata,setSearchpeopledata] = React.useState(people)

	const onchange = (e) => {
		if(e.target.value!==''){
			setSearchkey(e.target.value)
			setOpen(true)
			let suggetionValue = [];
			searchpeopledata.map(item=>{
		        let searchkeyval = e.target.value.toLowerCase();
		        let itemval = item.name.toLowerCase()
		        if(itemval.includes(searchkeyval)){
		          suggetionValue.push(item)
		        }
		    })
		    setSearchpeopledata(suggetionValue)
		}else{
			setOpen(false)
			setSearchkey("")
			setSearchpeopledata(people)
		}
	}
	console.log(open)
	return 	<aside>
	            <header>
	              <input 
	              	type="text"
	              	placeholder="search"
	              	onChange={onchange}
	              	value={searchkey}
	              />
	            </header>
	            {
	            	open ?
	            		<ul className="seachpopop">
	            		{
	            			searchpeopledata.map(item=>{
	            				return 	<li>
							               <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/1940306/chat_avatar_09.jpg" alt=""/>
							               <div>
							               	  <h2>{item.name}</h2>
							               	  {
							               	  	(item.onlineStatus) ?
							               	  		<h3>
									                    <span className="status green"></span>
									                    online
									                  </h3>
							               	  	:
							               	  		<h3>
									                    <span className="status orange"></span>
									                    offline
									                  </h3>
							               	  }

							                </div>
							            </li>
	            			})
	            		}
			            </ul>
	            	:null
	            }
	            <ul className="showuserlist">
	              <li>
	                <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/1940306/chat_avatar_09.jpg" alt=""/>
	                <div>
	                  <h2>Prénom Nom</h2>
	                  <h3>
	                    <span className="status green"></span>
	                    online
	                  </h3>
	                </div>
	              </li>
	              <li>
	                <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/1940306/chat_avatar_10.jpg" alt=""/>
	                <div>
	                  <h2>Prénom Nom</h2>
	                  <h3>
	                    <span className="status orange"></span>
	                    offline
	                  </h3>
	                </div>
	              </li>
	            </ul>
	        </aside>
}

export default Chatlist
