import React from 'react';


const Chat = (props) => {
	console.log('asd',props.state.userchat)
	return 	<main>
                <header>
                  <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/1940306/chat_avatar_01.jpg" alt=""/>
                  <div>
                    <h2>Chat with Vincent Porter</h2>
                    <h3>already 1902 messages</h3>
                  </div>
                  <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/1940306/ico_star.png" alt=""/>
                </header>
                <ul id="chat">
                {
                  props.state.userchat &&
                  props.state.userchat.map(item=>
                    <li key={item._id} className={`${item.receiver===props.receiver ? "you" : "me"}`}>
                      <div className="entete">
                        <span className="status green"></span>
                        <h2>Vincent</h2>
                        <h3>10:12AM, Today</h3>
                      </div>
                      <div className="triangle"></div>
                      <div className="message">
                        {item.message}
                      </div>
                    </li> 
                  )
                }

                  {
                    // <li className="me">
                    //   <div className="entete">
                    //     <h3>10:12AM, Today</h3>
                    //     <h2>Vincent</h2>
                    //     <span className="status blue"></span>
                    //   </div>
                    //   <div className="triangle"></div>
                    //   <div className="message">
                    //     Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor.
                    //   </div>
                    // </li>
                }
                </ul>
                <footer>
                	<form onSubmit={props.sendmsg}>
                		<input 
	                    	type="text" 
	                    	placeholder="Type your message"
	                    	value={props.state.message}
	                    	onChange={(e)=>props.onchangeHandler(e.target.value)}
                        style={{width:"80%"}}
	                    />
	                    <button style={{width:"20%"}} type="submit" onClick={props.sendmsg}>Send</button>
                	</form>
                </footer>
              </main>
}

export default Chat
