import React ,{Component} from "react";
import { connect } from "react-redux";
import Userlist from "./Userlist";
import Userchat from "./Userchat";
import {getuserlist,getalluser,addNewUser,getuserchat} from '../../actions/chatActions'
import './style.css'




class index extends Component {

	state = {
		message:"",
	    userchat:[],
	    chatusers:[],
	    userList:[],
	    open:false,
	    searchkey:'',
	    searchpeopledata:[],
	    suggetionvalue:[],
	    alluser:[],
	}

	componentDidMount(){
	    this.props.getuserlist(this.props.history)
	    this.props.getalluser()
		this.getuserchat(this.props.match.params.cid)
	}

	render(){
		// console.log(this.props.history)
		// console.log(this.props.chatlistdata)

		return (
		    <div className="container-fluid h-100">
				<div className="row justify-content-center h-100">
					<Userlist
						chatlistdata={this.props.chatlistdata}
						addNewUser={this.props.addNewUser}
					/>
					<Userchat
						chatlistdata={this.props.chatlistdata}
					/>
				</div>
			</div>
		  );	
	}

}

const mapStateToProps = (state, ownProps) => {
    return {
    	chatlistdata:state.chat
    }
};

const mapDispatchToProps = dispatch => {
    return {
        getuserlist: (payload,callback) => dispatch(getuserlist(payload,callback)),
        getalluser: (payload,callback) => dispatch(getalluser(payload,callback)),
        addNewUser: (payload,callback) => dispatch(addNewUser(payload,callback)),
        getuserchat: (payload,callback) => dispatch(getuserchat(payload,callback)),
    };
  };

export default connect(mapStateToProps,mapDispatchToProps)(index)
