import React,{Component} from 'react';
import axios from "axios";
import Chatlist from './Chatlist'
import Chat from './Chat'
import './style.css'

class ChatContainer extends Component{

  state = {
    massage:""
  }

  componentDidMount(){
    // console.log("okk")
  }

  sendmsg = (e) => {
    e.preventDefault();
    let payload = {
      message:this.state.message,
      attachment:'',
      receiver:"wq"
    }

    axios.post("http://localhost:8000/chatroom/savechat",payload,{
        headers:{
          Authorization:"Bearer "+localStorage.getItem("CC_Token"),
        }
    })
      .then(res=>{
        setAlluser(res.data)
        let firstId = res.data[0]._id;

    })
      .catch(err=>{
        setTimeout(getUsers,3000)
      })

    this.setState({massage:""})
  }

  onchangeHandler = (val) => {
    this.setState({massage:val})
  }

  render(){
    return(
          <div id="container">
              <Chatlist/>
              <Chat sendmsg={this.sendmsg} state={this.state} onchangeHandler={this.onchangeHandler}/>
          </div>
      );
    }
}


export default ChatContainer
