import React ,{Component,useRef,useState} from "react"

export default function Newcompo({handleClick}){
	const inputRef = useRef("")
	const inputRef1 = useRef("")
	const [state,setState] = useState({
		isEven:"",
		isPrime:"",
		number:"",
		number1:"",

	})





	return(
		<div>
			<p>Check odd or even number</p>
			<input type="number" ref={inputRef}/>
			<button onClick={handleClick}>Click me</button>
			{
				state.number &&
					<p>This number {state.number} is {state.isEven}</p>
			}
			<br/>
			<br/>
			<p>Check prime number</p>
			<input type="number" ref={inputRef1}/>
			<button onClick={handleClick}>Click me</button>
			{
				state.number1 &&
					<p>This number {state.number1} is {state.isPrime ? "prime" : "non prime"}</p>
			}
		</div>
	)
}
