import React ,{Component,useRef,useState} from "react"
import Form from "./Form"
import Button from "./Button"
import Result from "./Result"


export default function index() {

	const inputRef = useRef("")
	const inputRef1 = useRef("")

	const [state,setState] = useState({
		isEven:"",
		isPrime:"",
		number:"",
		number1:"",

	})


	function handleClick() {
		let num = inputRef.current.value%2;
		let isEven = "even"
		if(num===1){
			isEven="odd"
		}
		setState({
			...state,
			number:inputRef.current.value,
			isEven  :isEven
		})
	}

	function checkPrime() {
		let val = parseInt(inputRef1.current.value);
		let isPrime = true;

		if(val===1){
			alert("1 is neither prime nor composite number.");
		}else if(val>1){
			for (let i = 2; i < val; i++) {
				if(val%i===0){
					isPrime=false;
					break;
				}
			}

			setState({
				...state,
				number1:inputRef1.current.value,
				isPrime  :isPrime
			})
		}
	}

	return <div>
				<Form handleClick={handleClick} inputRef={inputRef}/>
				<Button />
				<Result />
				<Form handleClick={checkPrime} inputRef={inputRef1}/>
				<Button />
				<Result />
			</div>
}
