import React ,{Component,useRef,useState} from "react"
import Form from "./Form"
import Button from "./Button"
import Result from "./Result"


export default function Main() {

	const [state,setState] = useState({
		oddEven:"",
		isPrimeNo:"",
		isEven:"",
		isPrime:"",
	})

	const handlerChange = (value,name) => {
		setState({...state,[name]:value})
	}


	function handleClick() {
		let val = state.oddEven%2;
		let isEven = "even"
		if(val===1){
			isEven="odd"
		}
		setState({
			...state,
			isEven:isEven
		})
	}

	function checkPrime() {
		console.log(state.isPrimeNo)
		let val = parseInt(state.isPrimeNo);
		let isPrime = true;

		if(val===1){
			alert("1 is neither prime nor composite number.")
		}

		// if(val===1){
		// 	alert("1 is neither prime nor composite number.");
		// }else if(val>1){
		// 	for (let i = 2; i < val; i++) {
		// 		if(val%i===0){
		// 			isPrime=false;
		// 			break;
		// 		}
		// 	}

		// 	setState({
		// 		...state,
		// 		// number1:inputRef1.current.value,
		// 		isPrime  :isPrime
		// 	})
		// }
	}

	return <div>
				<Form handlerChange={handlerChange} name="oddEven"/>
				<br/>
				<Result type={state.isEven}/><br/>
				<Button handleClick={handleClick} >Check odd or even no</Button>
				<br/><br/><br/>
				<Form handleClick={checkPrime} handlerChange={handlerChange} name="isPrimeNo"/>
				<br/>
				<Result type={state.isPrime}/><br/>
				<Button handleClick={checkPrime} >Check prime no.</Button>
			</div>
}
